import { useState } from 'react'
import axios from 'axios'

const DownloadFile = () => {
    const [filename, setFilename] = useState("")
    const [file, setFile] = useState("")

    const download = async () => {
        const url = `https://iebfvkv2nk.execute-api.us-east-2.amazonaws.com/DEV/file-download-presignedurl?file_name=${filename}`

        const result = await axios.get(url)
        console.log("result: ", result)
        const getUrl = result.data.url

        try {
            const downloadResult = await axios.get(getUrl)
            console.log("uploadResult", downloadResult.data)
            setFile(downloadResult.data)
        } catch (e) {
            console.log("Error downloading Object URL", e)
        }

    }


    const handleSubmit = (e) => {
        e.preventDefault()
        download()

    }

    return (
        <div>
            <div>DownloadFile</div>
            <form onSubmit={handleSubmit}>
                <input className="w-full border rounded" type="text" onChange={(e) => setFilename(e.target.value)} />
                <button className="bg-blue-500 text-white w-[100px] rounded" type="submit">Get File</button>
            </form>
            <div>
                <img src={file} alt="image" />
            </div>
        </div>
    )
}

export default DownloadFile
